var tableIndex = env.get("tableIndex") || "$topic";
var tableDataProp = env.get("tableDataProp") || "row";
var tableContext = env.get("tableContext");
var dashboard = env.get("dashboard");
var maxRows = env.get("maxRows") || 0;
var maxStore = env.get("maxStore") || 0;

if (!tableContext.hasOwnProperty("tableData") || !tableContext.hasOwnProperty("tableConfig")) {
    status.text="tableContext not defined";
    node.error(status.text);
